// SPDX-License-Identifier: GPL-3.0

pragma solidity >=0.7.0 <0.9.0;

contract CampaignFactory {

    event CampaignCreated(address indexed creator, address createdCampaign, uint indexed timeCreated);

    function createCampaign(uint minimumValue) public {
        Campaign campaign = new Campaign(minimumValue, msg.sender);
        address adr  = address(campaign);
        emit CampaignCreated(msg.sender, adr, block.timestamp);
    }
}


contract Campaign {

    struct Request {
        string description;
        uint value;
        address recipient;
        bool complete;
        uint approvalCount;
        bool exists;
        mapping(address => bool) approvals;
    }

    address public manager;
    uint public minimumContribution;

    uint public approversCount;
    mapping(address => bool) public approvers;

    uint public numRequests;
    mapping(uint => Request) public requests;

    modifier restricted() {
        require(msg.sender == manager, "Only manager access allowed");
        _;
    }

    constructor(uint minimum, address creator) {
        manager = creator;
        minimumContribution = minimum;
    }

    function contribute() public payable {
        require(msg.value >= minimumContribution);
        approvers[msg.sender] = true;
        approversCount++;
    }

    function createRequest(string memory description, uint value, address recipient)
     public restricted {
         Request storage r = requests[numRequests++];
         r.description = description;
         r.value = value;
         r.recipient = recipient;
         r.complete = false;
         r.approvalCount = 0;
         r.exists = true;
    }

    function approveRequest(uint index) public {
        Request storage current = requests[index];
        require(approvers[msg.sender], "Requests can only be approved by investors");
        require(current.exists, "Request does not exist");
        require(!current.approvals[msg.sender], "Current investor has already voted on this request");

        current.approvals[msg.sender] = true;
        current.approvalCount++;
    }

    function finaliseRequest(uint index) public restricted {
        Request storage current = requests[index];
        require(current.approvalCount > approversCount / 2, "Failed to acheive majority in votes");
        require(!current.complete, "Request is already complete");

        payable(current.recipient).transfer(current.value);
        current.complete = true;
    }
}
